sys_script_include.script:
var DisposalTOStageHelper = Class.create();
DisposalTOStageHelper.prototype = {
    initialize: function() {
    },

	/*
     * Helper method for transferOrder
     */
    getStageTO: function(stage) {
        if (stage == 'new' || stage == 0)
            return {sequence:0, u_stage:'new'};
        else if (stage == 'in_transit' || stage == 1) 
            return {sequence:1, u_stage:'in_transit'};
        else if (stage == 'warehoused' || stage == 2)
            return {sequence:2, u_stage:'warehoused'};
        else if (stage == 'pending_data_wipe' || stage == 3)
            return {sequence:3, u_stage:'pending_data_wipe'};
		else if (stage == 'closed' || stage == 4)
            return {sequence:4, u_stage:'closed'};
        //else if (stage == 'cancelled' || stage == 6)
            //return {sequence:6, u_stage:'cancelled'};
    },

    /*
     * Helper method to get numeric stages for transferOrderLine
     */
    getStageTOL: function(stage) {
        if (stage == 'new' || stage == 0)
            return {sequence:0, u_stage:'new'};
        else if (stage == 'in_transit' || stage == 1)
            return {sequence:1, u_stage:'in_transit'};
        else if (stage == 'warehoused' || stage == 2)  
            return {sequence:2, u_stage:'warehoused'};
        else if (stage == 'received' || stage == 3) 
            return {sequence:3, u_stage:'received'};
        else if (stage == 'completed' || stage == 4)
            return {sequence:4, u_stage:'completed'};
       // else if (stage == 'cancelled' || stage == 5)
            //return {sequence:5, stage:'cancelled'};
    },

    type: 'DisposalTOStageHelper'
};
