sys_script_include.script:
var DisposalTOReceiver = Class.create();
DisposalTOReceiver.prototype = {
    initialize: function() {
    },

	receiveAssets : function(sysId, received) {
		var transferOrderLine = new GlideRecord('u_alm_disposal_transfer_order_line');
		transferOrderLine.get(sysId);
		if (transferOrderLine.u_quantity_remaining == '')
			transferOrderLine.u_stage = 'received';
		transferOrderLine.update();

		if (transferOrderLine.u_stage == 'received') {
			var DisposalTOStageHandler = new DisposalTOStageHandler();
			DisposalTOStageHandler.pushStageFromTOL(transferOrderLine,
					'received');
		}
	},
    type: 'DisposalTOReceiver'
};
