sys_ui_action.script:
// declare variables as arrays and also update the current fields before begining the script
current.update();
var serialNo = [], 
	notValid = [], 
	isValid = [];
var allSerialNos = current.serial_numbers;
var wrongString = "";

// push the serial numbers into the current array to loop through using comma as a delimiter
serialNo = allSerialNos.split(",");

// check to see if the specific serial number is part of the CMDB table
// using if/then
for (i = 0; i < serialNo.length; i++) {

	if (serialNo[i] != "") {
		var newArray = [];
		var CIGR = new GlideRecord("alm_asset");
		CIGR.addQuery('serial_number',serialNo[i]);
		CIGR.query();

		// this was new...and may have broken it!
		while (CIGR.next()) {
			newArray.push(CIGR.next());
		}

		var rowCount = newArray.length;
		gs.addInfoMessage(rowCount + "This is how many serial numbers we have.");
		// end of broken 

		// while there still is another row, set the sys_id to sysID
		while (CIGR.next()) {
			var sysID = CIGR.sys_id;
		}

		if (rowCount == 0) {
			// is not valid
			var CIGRNot = new GlideRecord("u_alm_disposal_transfer_order");
			CIGRNot.addQuery('sys_id',current.disposal_transfer_order.sys_id);
			CIGRNot.query();
			CIGRNot.next();

			var addtItems = CIGRNot.u_additional_items;		

			// add additional items field within field
			CIGRNot.u_additional_items = addtItems + ", " + serialNo[i];
			CIGRNot.update();

		} else {
			// is valid
			gs.addInfoMessage("This is right yay");
			var CIGRExisting = new GlideRecord("u_alm_disposal_transfer_order_line");
			CIGRExisting.addQuery("u_asset","388e55214f6f0200af76d0af0310c7c0");
			CIGRExisting.query();
			CIGRExisting.next();
			var rowCount2 = CIGRExisting.getRowCount();

			if (rowCount2 == 0) {
				// if no records are found, run this script to add it to the list
				var CIGR2 = new GlideRecord("u_alm_disposal_transfer_order_line");
				CIGR2.active = true;
				CIGR2.u_disposal_transfer_order = current.disposal_transfer_order;
				CIGR2.u_asset = sysID;
				CIGR2.update();
			} else {
				// otherwise, let the user know that a transfer order for this serial number was found
				// and was skipped as part of this loop run-through

				// loop through all of the serial numbers and concatenate a string
				// that shows the serial numbers that were already found in the transfer order table
				// as an addInfoMessage
				for (j = 1; j < rowCount2; j++) {
					var k = j - 1;
					wrongString += serialNo[k] + "is already in transfer order and was skipped.\n";
				}
				gs.addInfoMessage(wrongString);
			}
			// action.setRedirectURL("uri=u_alm_disposal_transfer_order.do?sys_id=" + current.disposal_transfer_order.sys_id);
			// action.setRedirectURL(current.disposal_transfer_order);
		}
	} 
}	
// redirect the user back to the form
current.state = "complete";
current.update();
